(71, 'FDP on Data Science & Machine Learning', '2021-12-06', '2021-12-10', 'The Department of IT, SRKREC is organizing an Online Short-Term Course on Data Science & Machine Learning from 6th  to 10th December 2021 in association with Center for Continuing Education, NIT Warangal.<br><br>\r\n\r\n<b>BROCHURE:</b> <a href=\'files/IT/DSML-STC_IT-SRKR-2021.pdf\' target=\'_new\'>Download Brochure</a>', 'event_details.php?id=71', NULL, '', 'IT', '2021-11-23 03:30:00'),
(72, 'CRUX', '2021-11-26', '2021-11-27', 'The Department of ECE, SRKREC Annual Project Expo - CRUX.<br><br>\r\n<b>Dates:</b> 26th & 27th November 2021', 'event_details.php?id=72', NULL, '', 'EC', '2021-11-23 03:30:00'),
(73, 'Freshers Orientation Programme', '2021-11-29', '2021-11-29', 'Freshers 2021 Orientation Programme.<br><br>\r\n<b>Dates:</b> 29th November 2021', 'event_details.php?id=73', NULL, '', '', '2021-11-29 03:30:00'),
(75, 'National Science Day 2022 Seminar', '2022-02-28', '2022-02-28', 'A one day National seminar on \"Science and Technology for Better Society\" is being conducted on Feb 28th 2022 on the occasion of National Science Day by department of Engineering Chemistry SRKREC. <br>\r\n\r\nThe program is intended for the first year B.Tech students of any branch. There is no registration fee for the registered candidates.\r\n<br><br>\r\n<b>Date:</b> 28th February 2022<br>\r\n\r\n<b>BROCHURE</b>: <a href=\'files/science_day_2022.pdf\' target=\'_new\'>DOWNLOAD</a>', 'event_details.php?id=75', NULL, '', 'BS', '2022-02-22 03:30:00'),
(74, 'FDP on on Trends and Challenges in Advanced VLSI System Design', '2022-02-21', '2022-02-26', 'ECE Department of S.R.K.R Engineering College is organizing an FDP on Trends and Challenges in Advanced VLSI System Design from 21st Feb 2022 to 26th Feb 2022. \r\n<br><br>\r\n<b>Dates:</b> 21st to 26th February 2022\r\n\r\n', 'event_details.php?id=74', NULL, '', 'EC', '2022-02-22 03:30:00'),
(76, 'FREEDOM FEST 2022', '2022-03-11', '2022-03-12', '<b>SWECHA FREEDOM FEST  2022</b><br><br>\r\nSwecha Freedom Fest at SRKR ENGINEERING COLLEGE Bhimavaram in association with Technology Center SRKR <br><br>\r\n\r\nWorkshops<br>\r\nTalks<br>\r\nTechnical and Non Technical Events<br><br>\r\n\r\n<b>Register Online at</b>\r\n<a href=\'http://swechaap.org/events\' target=\'_new\'>http://swechaap.org/events/ffestsrkr</a><br><br>\r\n<img src=\'img/events/freedomfest2022.jpg\' width=\'200px\'></a>', 'event_details.php?id=76', NULL, '', 'TC', '2022-03-02 03:30:00'),
(77, 'ECE SDP 2022', '2022-03-21', '2022-03-25', '<b>Student Development Programme  2022</b><br><br>\r\nby ECE Department during 21st to 25th March 2022<br><br>\r\n\r\n', 'event_details.php?id=77', NULL, '', 'EC', '2022-03-18 03:30:00'),
(78, 'Talk on Character Building', '2022-03-11', '2022-03-11', '<b>A Talk on Character Building</b> has been given by Dr. Kuppa Viswanadha Sarma. The event is organized by \nby Maths & Humanities in associaton with Dept of Chemistry on 11th March 2022<br><br>\n\n', 'event_details.php?id=78', NULL, '', 'BS', '2022-03-12 03:30:00'),
(79, 'EEE Tech Symposium APOORVA', '2022-03-26', '2022-03-27', 'The Dept of EEE at SRKR Engg College is conducting a Technical Symposium named as APOORVA on 26th & 27th of March, 2022.', 'event_details.php?id=79', NULL, '', 'EE', '2022-03-22 03:30:00'),
(80, 'IT SDP on AI & ML in IOT', '2022-03-21', '2022-03-25', 'The Dept of IT at SRKR Engg College is conducting a Workshop on Artificial Intelligence and Machine Learning based on IOT during 21st to 25th March, 2022.<br>', 'event_details.php?id=80', NULL, '', 'IT', '2022-03-22 03:30:00'),
(81, 'SRKREC IDEATHON 2022', '2022-04-06', '2022-04-30', 'The Technology Centre, SRKREC is presenting IDEATHON 2022, an idea pitching contest exclusive for SRKREC students (any branch and year) where the students can grab a chance to win:<br><br>\n\n-Paid Internship<br> \n-Exclusive Membership to IDEA Lab<br>\n-Funding & Guidance for your Own startup<br>\n-Become CEO/CFO of an On-campus Startup<br>\n-Cash Prizes & Rewards worth Rs.5000/-<br><br> \n\nInterested students can fill the registration form @ <a href=\'http://www.srkrec.edu.in/ideathon\' target=\"_new\">www.srkrec.edu.in/ideathon</a><br><br>\n\n<b>NO REGISTRATION FEE</b><br><br>\n\nFor queries, Contact Technology Centre or Call 7569560657, 9293940004.', 'event_details.php?id=81', NULL, 'http://www.srkrec.edu.in/ideathon', 'TC', '2022-04-06 03:30:00'),
(82, 'ISTE NIPUNA  2022', '2022-04-29', '2022-04-30', '<b>ISTE NIPUNA  2022</b><br><br>\nNIPUNA, the National Level Techno Management Fest of ISTE Student Chapter of SRKREC is to be conducted during 29th and 30th of April 2022.<br><br>\n<a href=\'img/events/nipuna22poster.jpg\' target=\'new\'>\n<img src=\'img/events/nipuna22poster.jpg\' width=\'400px\'></a>', 'event_details.php?id=82', NULL, 'http://www.nipuna22.srkriste.com/', '', '2021-04-10 03:30:00');
